import React from "react";
import { Image } from "./image"; // Assuming Image is a separate component to display images
import "./Dashboard.css"; // CSS file for Dashboard

export const Dashboard = (props) => {
  return (
    <div id="dashboard" className="text-center">
      <div className="container">
        <div className="section-title">
          <h2>Dashboard</h2>
          <p>
            This dashboard displays two images side by side with options for download and save.
          </p>
        </div>

        <div className="row">
          {/* Only showing 2 images side by side */}
          {props.data && props.data.length >= 2 ? (
            <div className="images-container">
              <div className="col-md-6">
                <Image
                  title={props.data[0].title}
                  largeImage={props.data[0].largeImage}
                  smallImage={props.data[0].smallImage}
                />
              </div>
              <div className="col-md-6">
                <Image
                  title={props.data[1].title}
                  largeImage={props.data[1].largeImage}
                  smallImage={props.data[1].smallImage}
                />
              </div>
            </div>
          ) : (
            "Loading..."
          )}
        </div>

        <div className="button-container">
          <button className="btn btn-custom">Download</button>
          <button className="btn btn-custom">Save</button>
        </div>
      </div>
    </div>
  );
};
