import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Navigation } from "./components/navigation"; // Navbar
import { Header } from "./components/header"; // Landing page Header
import { Features } from "./components/features"; // Landing page Features section
import { About } from "./components/about"; // Landing page About section
import { Login } from "./components/login"; // Landing page Services section
import { Contact } from "./components/contact"; // Footer

import { SignUp } from "./components/SignUp"; // Sign Up page
import JsonData from "./data/data.json";
import SmoothScroll from "smooth-scroll";
import "./App.css";

// Smooth scroll setup
export const scroll = new SmoothScroll('a[href*="#"]', {
  speed: 1000,
  speedAsDuration: true,
});

const App = () => {
  const [landingPageData, setLandingPageData] = useState({});

  useEffect(() => {
    setLandingPageData(JsonData);
  }, []);

  return (
    <Router>
      {/* Common Navigation for all routes */}
      <Navigation />

      <Routes>
        {/* Main Landing Page Route */}
        <Route
          path="/"
          element={
            <div>
              <Header data={landingPageData.Header} />
              <Features data={landingPageData.Features} />
              <Login data={landingPageData.Login} />
              <About data={landingPageData.About} />
              {/* Common Footer (Contact component) */}
              <Contact data={landingPageData.Contact} />
            </div>
          }
        />

        {/* Login Page Route */}
        <Route
          path="/login"
          element={
            <div>
              <Login />
              {/* Common Footer (Contact component) */}
              <Contact data={landingPageData.Contact} />
            </div>
          }
        />

        {/* Sign Up Page Route */}
        <Route path="/signup" element={<SignUp />} />
      </Routes>
    </Router>
  );
};

export default App;
