import React, { useState } from "react";
import { Footer } from "./footer";
import axios from "axios";

export const Dashboard = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [lowResImage, setLowResImage] = useState(null); // Image to display after upload
  const [highResImage, setHighResImage] = useState(null); // Restored image URL
  const [errorMessage, setErrorMessage] = useState("");

  // Handle file selection
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setErrorMessage(""); // Clear any previous error
    if (event.target.files[0]) {
      setLowResImage(URL.createObjectURL(event.target.files[0]));
    }
  };

  // Handle file upload
  const handleFileUpload = async () => {
    if (!selectedFile) {
      setErrorMessage("Please select a file before uploading.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      // Make a POST request to the Flask backend
      const response = await axios.post("http://127.0.0.1:5000/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      // Update the UI with the restored image
      if (response.data.restored_image) {
        setHighResImage(`http://127.0.0.1:5000/${response.data.restored_image}`);
        setErrorMessage(""); // Clear any error
      } else {
        setErrorMessage("Failed to restore the image.");
      }
    } catch (error) {
      setErrorMessage("An error occurred during the upload process.");
      console.error(error);
    }
  };

  return (
    <div id="dashboard" className="text-center">
      <div className="container">
        <div className="section-title">
          <h2>Dashboard</h2>
        </div>

        <div className="row">
          <div className="images-container">
            {/* Display the selected low-res image */}
            <div className="col-md-6">
              <h3>Low-Resolution:</h3>
              {lowResImage ? (
                <img src={lowResImage} className="img-responsive" alt="Low-Resolution Preview" />
              ) : (
                <p>No image selected</p>
              )}
            </div>

            {/* Display the restored high-res image */}
            <div className="col-md-6">
              <h3>High-Resolution:</h3>
              {highResImage ? (
                <img src={highResImage} className="img-responsive" alt="Restored Image" />
              ) : (
                <p>Restored image will appear here after upload.</p>
              )}
            </div>
          </div>
        </div>

        <div className="button-container">
          <div className="upload-section">
            <input type="file" accept=".jpeg,.jpg,.png" onChange={handleFileChange} />
            <button className="btn btn-custom" onClick={handleFileUpload}>
              Upload
            </button>
          </div>

          {/* Download & Save buttons */}
          <button
            className="btn btn-custom"
            disabled={!highResImage}
            onClick={() => {
              const link = document.createElement("a");
              link.href = highResImage;
              link.download = "restored_image.png";
              link.click();
            }}
          >
            Download
          </button>
          <button className="btn btn-custom" disabled={!highResImage}>
            Save
          </button>
        </div>

        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </div>
      <Footer />
    </div>
  );
};
